import React, {Component} from 'react';
import {Layout, Menu, Image} from 'antd';
import {Link,Switch,Redirect,Route} from "react-router-dom";
import {MenuUnfoldOutlined, MenuFoldOutlined,FireOutlined} from '@ant-design/icons';
import ArticleList from '../pages/ArticleList/index';
import Login from "../pages/Login";
import Register from "../pages/Register";
import ArticleContent from "../pages/ArticleContent";
import './index.less'
const { Header, Sider, Content } = Layout;
class Home extends Component {
    state = {
        collapsed: false,
        data:{}
    };

    toggle = () => {
        this.setState({
            collapsed: !this.state.collapsed,
        });
    };
    render() {
        return (
            <Layout>
                <Sider trigger={null} collapsible collapsed={this.state.collapsed}>
                    <Image src="data:image/png;base64,"/>
                    <div className="logo" />
                    <Menu theme="dark" mode="inline" defaultSelectedKeys={['1']}>
                        <Menu.Item key="1" icon={<FireOutlined />}>
                            <Link to="/home/articleList">文章浏览</Link>
                        </Menu.Item>
                    </Menu>
                </Sider>
                <Layout className="site-layout" >
                    <Header className="site-layout-header" style={{ padding: 0 }}>
                        <h2 align="center">创作者之家</h2>
                        <div style={{position:'absolute', left:'85%', top:'1%'}}>
                            <Login/>&nbsp;
                            <Register/>
                        </div>
                        {React.createElement(this.state.collapsed ? MenuUnfoldOutlined : MenuFoldOutlined, {
                            className: 'trigger',
                            onClick: this.toggle,
                        })}
                    </Header>
                    <Content className="site-layout-concent" style={{margin: '24px 16px', padding: 24,}}>
                        <Switch>
                            <Route path="/home/articleList" component={ArticleList}/>
                            <Route path="/home/articleContent" component={ArticleContent}/>
                            <Redirect to="/home/articleList"/>
                        </Switch>
                    </Content>
                </Layout>
            </Layout>
        );
    }
}
export default Home;